#include <bits/stdc++.h>

using namespace std;

const int N = 44;

int a[N];

int main() {
	ios_base :: sync_with_stdio(0); cin.tie(0); cout.tie(0);
	int test;

	cin >> test;
	while(test --) {
		int n;
		cin >> n;
		int min_a = 1e9;
		for(int i = 1; i <= n; i ++) {
			cin >> a[i];
			min_a = min(min_a, a[i]);
		}
		int res = 0;
		for(int i = 1; i <= n; i ++) {
			res = __gcd(res, a[i] - min_a);
		}
		if(!res) cout << -1 << '\n';
		else cout << res << '\n';
	}
}
