#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
char s[200010],t[200010];
int f[200010];
inline void work()
{
    int n,k;
    scanf("%d%d",&n,&k);
    scanf("%s",s+1);
    for (int i=1;i<=n;i++)
    {
        if (s[i]=='R')s[i]=0;
        else if (s[i]=='G')s[i]=1;
        else if (s[i]=='B')s[i]=2;
    }
    int ans=k+1;
    for (int p=1;p<=3;p++)
    {
        for (int i=1;i<=n;i++)t[i]=(i+p)%3;
        for (int i=1;i<=n;i++)f[i]=t[i]!=s[i];
        int sum=0;
        for (int i=1;i<k;i++)sum+=f[i];
        for (int i=k;i<=n;i++)
        {
            sum+=f[i];
            sum-=f[i-k];
            ans=min(ans,sum);
        }
    }
    printf("%d\n",ans);
}
int main()
{
    int T;scanf("%d",&T);
    while (T--)work();
    return 0;
}
