// Problem: E. Anna and the Valentine's Day Gift
// Contest: Codeforces - Codeforces Round 925 (Div. 3)
// URL: https://codeforces.com/contest/1931/problem/E
// Memory Limit: 256 MB
// Time Limit: 2000 ms
// 
// Powered by CP Editor (https://cpeditor.org)

#include <bits/stdc++.h>
#define all(s) s.begin(), s.end()
using namespace std;
using ll = long long;
using ull = unsigned long long;

const int _N = 1e5 + 5;

int T;

void solve() {
	int n, m; cin >> n >> m;
	vector<string> a(n + 1);
	for (int i = 1; i <= n; i++) cin >> a[i];
	ll sum = 0;
	vector<int> p;
	for (int i = 1; i <= n; i++) {
		sum += a[i].length();
		int k = a[i].length();
		reverse(all(a[i]));
		int cnt = 0;
		while (cnt < k && a[i][cnt] == '0') cnt++;
		p.push_back(cnt);
	}
	int k = p.size();
	sort(all(p), greater<int>());
	for (int i = 0; i < k; i++) {
		if (!(i & 1)) sum -= p[i];
	}
	cout << (sum > m ? "Sasha" : "Anna") << '\n';
	return;
}

int main() {
	ios::sync_with_stdio(false), cin.tie(0), cout.tie(0);
	cin >> T;
	while (T--) {
		solve();
	}
}
