#include<bits/stdc++.h>
#define maxn 200010
using namespace std;

char a[maxn],b[maxn];
int c[maxn],d[maxn];
int dir[maxn],ans[maxn];
int main(){
    int n;
    scanf("%d",&n);
    scanf("%s%s",a,b);
    for(int i=0;i<n;i++){
        c[i+1]=a[i]-'a';
    }
    for(int i=0;i<n;i++){
        d[i+1]=b[i]-'a';
    }
    for(int i=n;i>=1;i--){
        //cout<<d[i]+c[i]<<endl;
        if((dir[i]+d[i]+c[i])>25){
            dir[i-1]++;
            dir[i]=dir[i]+d[i]+c[i]-26;
        }
        else{
            dir[i]+=d[i]+c[i];
        }
        //cout<<dir[i]<<endl;
    }

    if(dir[n]%2==1){
        int tmp=n;
        while(dir[tmp]==26&&tmp>0){
            dir[tmp-1]++;
            tmp--;
        }
    }

    for(int i=0;i<=n;i++){
        if(dir[i]%2==0){
            ans[i]=dir[i]/2;
        }
        else{
            dir[i+1]+=26;
            dir[i]--;
            ans[i]=dir[i]/2;
        }
    }
    /*for(int i=1;i<=n;i++){
        cout<<c[i]<<" ";
    }
    cout<<endl;
    for(int i=1;i<=n;i++){
        cout<<d[i]<<" ";
    }
    cout<<endl;
    for(int i=1;i<=n;i++){
        cout<<ans[i]<<" ";
    }
    cout<<endl;*/
    for(int i=1;i<=n;i++){
        cout<<char(ans[i]+'a');
    }
    cout<<endl;
    return 0;
}
