#include<bits/stdc++.h>
using namespace std;
#pragma GCC optimize("Ofast","inline","-ffast-math")
#pragma GCC target("avx,sse2,sse3,sse4,mmx")

#define int long long
int n,k,a[200001],f[200001];
vector<int> P[200001];
int get(int x) {
   if(f[x]!=-1) return f[x];
   if(P[x].empty()) return a[x];
   int s=0;
   for(int i:P[x])
      s+=get(i);
   return f[x]=min(a[x],s);
}
void Delta() {
   vector<int> value;
   cin >> n >> k;
   for(int i=1;i<=n;++i) {
      cin >> a[i];
      f[i]=-1;P[i].clear();
   }
   for(int i=1,x;i<=k;++i)  {
      cin >> x;a[x]=0;
   }
   for(int i=1,x;i<=n;++i) {
      cin >> x;
      for(int j=1,y;j<=x;++j) {
         cin >> y;
         P[i].push_back(y);
      }
   }
   for(int i=1;i<=n;++i) cout << get(i) << ' ';
   cout << endl;
}
signed main() {
   ios_base::sync_with_stdio(0);
   cin.tie(0);cout.tie(0);
   int T;cin >> T;
   while(T--) Delta();
}
