#include<bits/stdc++.h>
using namespace std;
#define PB push_back
#define LL long long
#define PII pair<int,int>
#define FI first
#define SE second
const int INF=0x3f3f3f3f;
const int N=1e6+7,mod=1e9+7;
int n,m;
int a[N];
int s[N];
LL ans;
int main()
{
    cin>>n>>m;
    for(int i=0;i<n;i++){
        for(int j=0;j<m;j++){
            scanf("%d",&a[i*m+j]);
        }
    }
    for(int j=0;j<m;j++){
        for(int i=0;i<n;i++)s[i]=0;
        for(int i=0;i<n;i++){
            if(a[i*m+j]%m==(j+1)%m&&a[i*m+j]<=n*m){
                //cout<<"fuck "<<i*m+j<<' '<<a[i*m+j]<<' '<<(i*m+j-a[i*m+j]+n*m)%(n*m)/m<<endl;
                s[(i*m+j+1-a[i*m+j]+n*m)%(n*m)/m]++;
            }
        }
        int add=INF;
        for(int i=0;i<n;i++){
            //cout<<s[i]<<endl;
            add=min(add,i+n-s[i]);
        }
        //cout<<"ADD="<<add<<endl;
        ans+=add;
    }
    cout<<ans;
    return 0;
}
