#include <bits/stdc++.h>
using namespace std;
#define rep(i,h,t) for (int i=h;i<=t;i++)
#define dep(i,t,h) for (int i=t;i>=h;i--)
#define ll __int128
const int N=3e5;
long long a[N],b[N];
ll c[N];
ll gcd(ll x,ll y)
{
	if (!y) return x;
	return gcd(y,x%y);
}
int main()
{
	ios::sync_with_stdio(false);
	int T;
	cin>>T;
	while (T--)
	{
		int n;
		cin>>n;
		rep(i,1,n) cin>>a[i];
		c[1]=a[1]-a[n];
		rep(i,2,n) c[i]=a[i]-a[i-1];
		ll gg=(c[1]%n+n)%n,tt=0;
		rep(i,1,n)
		{
		  if ((c[i]%n+n)%n!=gg) tt=1;
	    }
		if(tt) {cout<<"NO"<<"\n"; continue;}
		ll ans2=0;
		rep(i,2,n) 
		  ans2+=(n-i+2)*(c[1]-c[i])/n;
		b[1]=(a[1]-ans2)/(n*(n+1)/2);
		rep(i,2,n)
		{
			b[i]=b[1]+(c[1]-c[i])/n;
		}
		rep(i,1,n) if (b[i]<=0||b[i]>1e9) tt=1;
		ll ans3=0;
		rep(i,1,n) ans3+=1ll*b[i]*(n-i+1);
		if(tt||ans3!=a[n]) {cout<<"NO"<<"\n"; continue;}
		cout<<"YES"<<endl;
		rep(i,1,n) cout<<b[i]<<" ";
		cout<<endl;
	}
	return 0;
}
