#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define pb push_back
#define INF 1000000005
#define LINF 1000000000000000005
#define MAXN 100005
#define pi pair<int,int>
#define pl pair<ll,ll>

int n,x,y,t[200005];

vector <int> g[200005];

void DFS(int nod){
    t[nod]=1;
    bool leaf=1;
    for(int i : g[nod]){
        if(!t[i]){
            leaf=0;
            DFS(i);
            if(t[i]==2) t[nod]=3;
            if(t[i]==3 && t[nod]!=3) t[nod]=4;
        }
    }
    if(leaf) t[nod]=2;
    else if(t[nod]==1) t[nod]=2;
}

int32_t main(){
    ios_base :: sync_with_stdio(0);
    cin >> n;
    for(int i=1;i<n;i++){
        cin >> x >> y;
        g[x].pb(y);
        g[y].pb(x);
    }
    DFS(1);
    t[1]=2;
    for(int i : g[1]) t[i]=2;
    int ans=0;
    for(int i=1;i<=n;i++){
        if(t[i]==3) ans++;
    }
    cout << ans;
}
