#include<bits/stdc++.h>
using namespace std;
#define LL long long
#define PII pair<int,int>
#define PB push_back
#define POP pop_back
#define FI first
#define SE second
#define endl '\n'
#define ls x<<1
#define rs x<<1|1
const LL N=2e6+7,mod=998244353,INF=1e9;
LL n,m;
LL a[N],b[N],c[N];
int main()
{
    cin>>n;
    for(LL i=1;i<=n;i++){
        scanf("%lld",&a[i]);
    }
    for(int i=1;i<=n;i++){
        scanf("%lld",&b[i]);
    }
    for(LL i=1;i<=n;i++){
        c[i]=i*(n-i+1ll)*a[i];
    }
    sort(c+1,c+1+n);
    sort(b+1,b+1+n,greater<LL>());
    LL ans=0;
    for(int i=1;i<=n;i++){
        ans+=c[i]%mod*b[i];
        ans%=mod;
    }
    cout<<ans;
    return 0;
}
