#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
#define rep(x,y) for(int x=0;x<(y);x++)
inline int in(){
  int x;
  cin >> x;
  return x;
}
const int N=2e5;
string a[N+5];
int n,f[N+5][26];
map<int,int> cnt;
void solve(){
  n=in();
  rep(i,n)cin>>a[i];
  rep(i,n)rep(j,int(a[i].size())){
    f[i][a[i][j]-'a']++;
  }
  ll ans=0;
  for(int bad=0;bad<26;bad++){
    cnt.clear();
    rep(i,n){
      if(f[i][bad])continue;
      int mask=0,n_mask=0;
      rep(j,26){
        if(j==bad)continue;
        if(f[i][j]%2){
          mask|=(1<<j);
        }
        else{
          n_mask|=(1<<j);
        }
      }
      ans+=cnt[n_mask];
      cnt[mask]++;
    }
  }
  cout<<ans<<"\n";
}
int main(){
  ios::sync_with_stdio(false);
  cin.tie(0);
  int T = 1;
  // cin >> T;
  while(T--) solve();
}
