#include<bits/stdc++.h>
using namespace std;

int t,n;
int a[200007],pos[200007];
int mins,maxs,curr;

long long ans;

long long calc(int l,int r,int len){
    len-=(r-l+1);
    long long lt=max(l-len,1),rt=min(n-len-(r-l+1)+1,l);
    return rt-lt+1;
}

int main(){

    ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);

    cin>>t;
    for(int i=1;i<=t;i++){
        cin>>n;
        for(int f=1;f<=n;f++){
            cin>>a[f];
            pos[a[f]]=f;
        }
        curr=ans=0;
        mins=n; maxs=-1;
        for(int f=1;f<=n;f++){
            if(f%2==1){
                mins=min(mins,pos[curr]);
                maxs=max(maxs,pos[curr]);
                curr++;
            }
            if(maxs-mins+1>f)continue;
            ans+=calc(mins,maxs,f);
        }
        cout<<ans<<"\n";
    }

    return 0;
}
