#include<bits/stdc++.h>
using namespace std;
#define TY int
#define IL inline
#define pb push_back
#define mod (TY)(1e9+7)
#define MAXN 200005
#define MAXM 200005
#define MAXK 27
#define INF (TY)(1e9)
#define block 300
#define For(i,a,b) for(TY i=(a);i<=(b);++i)
#define FOR(i,a,b) for(TY i=(a);i<(b);++i)
#define Rof(i,a,b) for(TY i=(a);i>=(b);--i)
#define ROF(i,a,b) for(TY i=(a);i>(b);--i)
IL TY qr(){
	TY x=0,f=1;char op=getchar();
	for(;op<'0'||op>'9';op=getchar())if(op=='-')f=-1;
	for(;op>='0'&&op<='9';op=getchar())x=x*10+(op^48);
	return x*f;
}IL bool ischar(char op){
	if(op>='a'&&op<='z')return true;
	if(op>='A'&&op<='Z')return true;
	return false;
}IL char getc(){
	char op=getchar();
	while(!ischar(op))op=getchar();
	return op;
}IL string qs(){
	string op="";char u=getchar();
	while(!ischar(u))u=getchar();
	while(ischar(u))op+=u,u=getchar();
	return op;
}IL void qw(TY x){
	if(!x){putchar('0');return;}
	if(x<0)putchar('-'),x=-x;
	if(x>=10)qw(x/10);putchar(x%10+'0');
}IL void qw(TY x,char op){qw(x),putchar(op);}
IL void ws(string s){FOR(i,0,s.size())putchar(s[i]);}
IL TY Ceil(TY a,TY b){return a/b+(a%b!=0);}
IL TY Mod(TY a){return (a>=mod?a-mod:a);}
IL TY Abs(TY a,TY b){return a>b?a-b:b-a;}
IL TY Pow(TY a,TY b){
	TY ans=1,base=a;
	while(b){
		if(b&1)ans=ans*base%mod;
		base=base*base%mod;b>>=1;
	}return ans;
}TY T,n,A,B,a[MAXN];
bool vis[1000005];
int main(){
    //freopen(".in","r",stdin);
    //freopen(".out","w",stdout);

    /* init */
	T=qr();while(T--){
		TY ans=INF,sum=0;
		A=qr();B=qr();n=qr();
		For(i,1,n)a[i]=qr(),sum+=a[i];
		For(i,0,n*10000)vis[i]=0;vis[0]=1;
		For(i,1,n)Rof(j,n*10000,a[i])vis[j]|=vis[j-a[i]];
		For(i,0,n*10000)if(vis[i])ans=min(ans,max(i==0?0:Ceil(i,A),sum-i==0?0:Ceil(sum-i,B)));
		qw(ans,'\n');
	}
    return 0;
}
