#include <bits/stdc++.h>

using namespace std;

int n,a,b;
string s;
const int N =  42;
int vis[N][N][N][N];
int stc[N], colors[N];
int ans;

void lui(int id, int mod_a, int mod_b, int num_a) {
    if (id == n) {
        if (mod_a == 0 && mod_b == 0 && num_a > 0 && num_a < n) {
            if (abs(n-2*num_a) < ans) {
                ans = abs(n-2*num_a);
                for (int i = 0 ; i < n; ++ i) {
                    colors[i] = stc[i];
                }
            }
        }

        return ;
    }

    if (vis[id][mod_a][mod_b][num_a] == 1) {
        return ;
    }

    vis[id][mod_a][mod_b][num_a] = 1;

    stc[id] = 0;
    lui(id+1, (mod_a*10 + s[id]-'0') % a, mod_b, num_a+1);
    stc[id] = 1;
    lui(id+1, mod_a, (mod_b*10+s[id]-'0')%b, num_a);
    return;
}

int main() {

    int t;
    cin >> t;
    while (t--) {
        cin >> n >> a >> b;
        cin >> s;
        memset(vis, 0 , sizeof(vis));
        ans = 1e9;
        lui(0, 0, 0, 0);
        if (ans != 1e9) {
            for (int i = 0 ; i < n ; ++ i) {
                if (colors[i] == 0) {
                    cout << "R";
                } else {
                    cout << "B";
                }
            }
            cout << endl;
        }  else {
            cout << -1 << endl;
        }
    }
    return 0;
}
