using namespace std;
namespace io
{
	template<typename T> inline void read(T&a){
		int c = getchar(),sign = 0;
		a = 0;
		while(c < '0' || c >'9') sign += (c == '-'),c = getchar();
		while (c >='0' && c <='9') a = (a << 1) + (a << 3) + c-48,c = getchar();
		if (sign&1) a = -a;
	}
	template<typename T,typename... _T>
	inline void read(T&a,_T& ...argc)
	{
		read(a);read(argc...);
	}
	template<typename T>
	inline void Write(T a)
	{
		if (a > 9) Write(a/10);
		putchar(a % 10 + '0');
	}
	template<typename T>
	inline void write(T a,char ch = ' ')
	{
		if (a < 0) putchar('-'),a = -a;
		Write(a);
		putchar(ch);
	}
	template<typename T> inline void Min(T&a,T b){if (a > b) a = b;}
	template<typename T> inline void Max(T&a,T b){if (a < b) a = b;}
	template<typename T,typename... _T> inline void Min(T&a,T b,_T ...c){Min(a,b);Min(a,c...);}
	template<typename T,typename... _T> inline void Max(T&a,T b,_T ...c){Max(a,b);Max(a,c...);}
}
using namespace io;
const int maxn= 1e6 + 3;
const int N = 4e5 + 3;
const int mod= 1e9 + 7 ;
int n,m,q;
int dem[N],id[N],chot[N],a[N],b[N];
int k,h;
ll t[N + 3];
string  s;
bool kt[N];
map<long long,int> maps;
vector<int> edge[N];
map<int,int> ::iterator it;
template<typename T>
void answer(T x) {cout << x;exit(0);}
void putstring(string x,char ch = ' ') {for (char c:x) putchar(c);putchar(ch);}

int dx[] = {-1,0,1,0},dy[] = {0,1,0,-1};
char c[] = {'U','R','D','L'};

struct dsu {
	vector<int> chot;
	int n;
	dsu(int n) : n(n) {
		chot.resize(n,-1);
	}
	int find(int u) {
		return chot[u] < 0 ? u : chot[u] = find(chot[u]);
	}
	void combine(int u,int v) {
		u = find(u);v = find(v);
		if (u != v) {
			if (-chot[u] < -chot[v]) swap(u,v);
			chot[u] += chot[v];
			chot[v] = u;
		}
	}
};

int main() {
	IOS;
	cin >> q;
	while (q--) {
		cin >> n >> m;
		auto con = [&](int i,int j) {
			return i * m + j;
		};
		vector<int> nxt(n*m);
		vector<bool> kt(n * m);
		for (int i = 0;i < n;i++) {
			string s;
			cin >> s;
			for (int j = 0;j < m;j++) kt[con(i,j)] = (s[j] == '0' ? true : false);
		}
		for (int i = 0;i < n;i++) {
			string s;
			cin >> s;
			for (int j = 0;j < m;j++) {
				for (int pos = 0;pos < 4;pos++) {
					if (s[j] == c[pos]) {
						int x = i + dx[pos];
						int y = j + dy[pos];
						nxt[con(i,j)] = (con(x,y));
						break;
					}
				}
			}
		}
		int ans = 0,black = 0;
		vector<int> vis(n * m);
		vector<vector<int>> time(n * m);
		vector<int> P(n * m);
		vector<int> d(n * m);
		vector<vector<int>> cycle;
		for (int i = 0;i < n * m;i++) {
			if (!vis[i]) {
				int u = i;
				stack<int> s;
				while (1) {
					s.emplace(u);
					if (vis[u] == 1) {
						s.pop();
						cycle.emplace_back(vector<int>());
						while (1) {
							int v = s.top();s.pop();
							cycle.back().emplace_back(v);
							++ans;
							if (kt[v]) time[v].emplace_back(0);
							vis[v] = 2;
							P[v] = v;
							d[v] = 0;
							if (u == v) break;
						}
						break;
					}
					else if (vis[u] == 2) break;
					vis[u] = 1;
					u = nxt[u];
				}
				if (s.empty()) continue;
				int root = s.top();
				while ((int)s.size()) {
					int v = s.top();s.pop();
					vis[v] = 2;
					P[v] = P[nxt[root]];
					d[v] = d[nxt[v]] + 1;
					if (kt[v]) time[P[v]].emplace_back(d[v]);
				}
			}
		}
		for (int i = 0;i < (int)cycle.size();i++) {
			set<int> s;
			int count = 0;
			for (auto&x : cycle[i]) {
				for (auto&u : time[x]) {
					s.insert((u + count) % (int)cycle[i].size());
				}
				count++;
			}
			black += (int)s.size();
		}
		cout << ans <<" " << black <<"\n";
	}
}

