        cnt[x]++;
        tot*=(cnt[x]+1);
    }
}
bool check(ll x)
{
    for(int i=2;i*i<=x;++i)
    {
        int c=0;
        while(x%i==0)   x/=i,c++;
        if(cnt[i]<c)    return false;
    }
    if(x!=1&&cnt[x]<1)  return false;
    return true;
}
void solve()
{
    n=read();   q=read();
