#include <bits/stdc++.h>
#define int long long
using namespace std;
int a[200005],b[200005],l[200005],r[200005];
signed main(int argc, char** argv) {
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--)
	{
		int n,m;
		cin >> n >> m;
		for(int i=1;i<=n;i++)
			cin >> a[i] >> b[i];
		for(int i=1;i<=n;i++)
		{
			int x=min(b[i],m);
			r[i]=(a[i]-(m-x))-(b[i]-x);
			x=min(a[i],m);
			l[i]=(a[i]-x)-(b[i]-(m-x));
//			cout << l[i] << "**" << r[i] << "\n";
		}
		int L=0,R=0;
		for(int i=1;i<=n;i++) L+=l[i],R+=r[i];
		int g=0;
		if(R<=0) g=R;
		else if(L>=0) g=L;
		else
		{
			if(L==0||R==0) g=0;
			else if(L%2==0) g=0;
			else if(L>0) g=1;
			else g=-1;
		}
		cout << max(g,-g) << "\n";// << "*\n";
		for(int i=1;i<=n;i++)
		{
			R-=r[i],L-=l[i];
			int x=min(b[i],m);
			int qwq=L+(a[i]-(m-x))-(b[i]-x)-g;
			if(qwq<=0)
			{
				L+=(a[i]-(m-x))-(b[i]-x),R+=(a[i]-(m-x))-(b[i]-x);
				cout << m-x << " " << x << "\n";
				continue;
			}
		//	cout << qwq << "()\n";
			if(qwq%2==0) x-=qwq/2;
			else x-=qwq/2+1;
			L+=(a[i]-(m-x))-(b[i]-x),R+=(a[i]-(m-x))-(b[i]-x);
			cout << m-x << " " << x << "\n";
		}
	}
	return 0;
}
