#include "bits/stdc++.h"

using namespace std;

using ll = long long;
const ll inf = 1e9;
const int md1 = 1e9+7;

#define all(v)                      v.begin(), v.end()
#define rall(v)                     v.rbegin(), v.rend()
#define sz(v)                       ((int)v.size())

#define forn(i,n)                   for(int i = 0; i < n; ++i)
#define forbe(i,b,e)                for(int i = b; i < e; ++i)

#define pb                          push_back

#define pry                         puts("YES")
#define prn                         puts("NO")
#define endl                        '\n'

#define fst                         first
#define scn                         second

const int N = 2e5;
int in[N], out[N];

vector<int> adj[N];
bitset<N> c;
int dp[N];

void dfs(int u){
    if (c[u]) 
        return;
    dp[u] = 1;
    c[u] = 1;
    if (out[u] < 2) 
        return;

    for(auto x : adj[u]){
        dfs(x);
        if (in[x] > 1)
            dp[u] = max(dp[u], 1 + dp[x]);
    }
}

void solve(){
    int n,m;
    cin >> n >> m;
    for(int i = 0; i < m; ++i){
        int a, b;
        cin >> a >> b;
        --a; --b;

        out[a]++;
        in[b]++;
        adj[a].pb(b);
    }

    int ans = 0;
    for(int i = 0; i < n; ++i){
        dfs(i);
    }

    for(int i = 0; i < n; ++i){
        ans = max(ans,dp[i]);
    }

    cout << ans << endl;
}

int32_t main(){
    #ifndef ONPC
        ios_base::sync_with_stdio(0);cin.tie(0);
    #else
        freopen("in", "r", stdin);
    #endif
    int t = 1;
    /* cin >> t; */
    while(t--)
        solve();
}
