#include<bits/stdc++.h>
using namespace std;

// xise me marko


int xino()
{
	string s;
	cin >> s;
	int val = 0;
	for (int i=0; i<s.size(); ++i)
    {
		if (s[i] == '1')
		val += (1<<i);
	}
	return val;
}

void solve()
{

  // νταικστρααα kratao left right bit
    int n, m;
	cin >> n >> m;
	int start = xino();
	vector<int> d(m), rm(m), ad(m);
	for (int i=0; i<m; ++i)
    {
		cin >> d[i];
		rm[i] = ~xino();
		ad[i] = xino();
	}
	vector<int> dist((1<<n), -1);
	dist[start] = 0;
	set<pair<int, int> > st;
	st.emplace(0, start);
	while (!st.empty())
    {
		int val = st.begin()->second;
		st.erase(st.begin());
		for (int i=0; i<m; ++i)
		{
			int val2 = val;
			val2 &= rm[i];
			val2 |= ad[i];
			if (dist[val2] == -1 || dist[val2] > dist[val] + d[i])
			{
				st.erase(make_pair(dist[val2], val2));
				dist[val2] = dist[val] + d[i];
				st.emplace(dist[val2], val2);
			}
		}
	}
	cout << dist[0] << endl;

}

int main()
{
    int t;
    cin>>t;
    while(t--)
    {
        solve();
    }
}
