#include<bits/stdc++.h>
using namespace std;
const int N = 200000 + 10;
const int inf = 0x3f3f3f3f;
typedef long long ll;
int a[N],f[N][2],g[N][2],ans[N];
int main(){
    ios::sync_with_stdio(0);
    int n; cin>>n;
    for(int i=1;i<=n;i++) cin>>a[i];
    f[1][0] = inf;
    f[1][1] = -inf;
    for(int i=2;i<=n;i++){
        //a[i-1] ... f[i-1][0]
        //f[i-1][1] ... a[i-1]
        f[i][0] = -inf;
        f[i][1] = inf;
        if(a[i]<f[i-1][0]){
            if(f[i][1]>a[i-1]) f[i][1] = a[i-1], g[i][1] = 0;
//            f[i][1] = min(f[i][1],a[i-1]);
        }
        if(a[i]>a[i-1]){
            if(f[i][0]<f[i-1][0]) f[i][0] = f[i-1][0], g[i][0] = 0;
//            f[i][0] = max(f[i][0],f[i-1][0]);
        }
        if(a[i]<a[i-1]){
            if(f[i][1]>f[i-1][1]) f[i][1] = f[i-1][1], g[i][1] = 1;
//            f[i][1] = min(f[i][1],f[i-1][1]);
        }
        if(a[i]>f[i-1][1]){
            if(f[i][0]<a[i-1]) f[i][0] = a[i-1], g[i][0] = 1;
//            f[i][0] = max(f[i][0],a[i-1]);
        }
    }
    if(f[n][0]==-inf&&f[n][1]==inf){
        cout<<"NO"<<endl;
        return 0;
    }
    cout<<"YES"<<endl;
    if(f[n][0]!=-inf){
        int t = 0;
        ans[n] = 0;
        for(int i=n;i>=1;i--){
            t = g[i][t];
            ans[i-1] = t;
        }
    }
    else{
        int t = 1;
        ans[n] = 1;
        for(int i=n;i>=1;i--){
            t = g[i][t];
            ans[i-1] = t;
        }
    }
    for(int i=1;i<=n;i++) cout<<ans[i]<<' ';
    cout<<endl;
}
