#include<bits/stdc++.h>
using namespace std;
#pragma GCC optimize("Ofast","inline","-ffast-math")
#pragma GCC target("avx,sse2,sse3,sse4,mmx")

#define int long long

const int MOD=998244353;
vector<int> Q[200001];
int n,m,q,a[200001],fa[200001],ans[200001];
int getfa(int x){if(fa[x]==x) return x;return fa[x]=getfa(fa[x]);}
void merge(int x,int y){fa[getfa(x)]=getfa(y);}
void Delta() {
   cin >> n >> m;
   for(int i=1;i<=n;++i) {
      cin >> a[i];
      fa[i]=i;Q[i].clear();
   }
   for(int i=1,u,v;i<=m;++i) {
      cin >> u >> v;
      Q[u].push_back(v);
      Q[v].push_back(u);
   }
   vector<array<int,4>> P;cin >> q;
   for(int i=1,u,v,e;i<=q;++i) {
      cin >> u >> v >> e;
      P.push_back({a[u]+e,u,v,i});
   }
   for(int i=1;i<=n;++i)
      P.push_back({a[i],-2147483648,i,-1});
   sort(P.begin(),P.end());
   for(array<int,4> i:P) {
      if(i[3]==-1)
         for(int j:Q[i[2]]) {
            if(a[j]<=a[i[2]])
               merge(i[2],j);
         }
      else
         ans[i[3]]=getfa(i[1])==getfa(i[2]);
   }
   for(int i=1;i<=q;++i)
      if(ans[i]) cout << "YES\n";
      else cout << "NO\n";
   cout << "\n";
}
signed main() {
   ios_base::sync_with_stdio(0);
   cin.tie(0);cout.tie(0);
   int T;cin >> T;
   while(T--) Delta();
}
