#include<bits/stdc++.h>
using namespace std;
const int K=333;
int random()
{
    return (int)(1.0*rand()/RAND_MAX*1e9);
}
map<int,int> ma;
int main()
{
    srand(time(0));
    int m=0,x;
    scanf("%d", &m);
    for(int i=1;i<=K;i++)
    {
        printf("+ %d\n",random());
        fflush(stdout);
        scanf("%d",&x);
        if(x>m) m=x;
    }
    for(int i=1;i<=K;i++)
    {
        printf("+ %d\n",1);
        fflush(stdout);
        scanf("%d",&x);
        if(ma[x])
        {
            printf("! %d",i-1);
            return 0;
        }
        else ma[x]=i;
    }
    printf("+ %d\n",m);
    fflush(stdout);
    scanf("%d",&x);
    if(ma[x])
    {
        printf("! %d",K+m-ma[x]);
        return 0;
    }
    for(int i=1;i<=K;i++)
    {
        printf("+ %d\n",K);
        fflush(stdout);
        scanf("%d",&x);
        if(ma[x])
        {
            printf("! %d",(i+1)*K+m-ma[x]);
            return 0;
        }
    }
    return 0;
}
