#include <iostream>

using namespace std;

int main() {
    int t;
    cin >> t;
    for (int i = 0; i < t; i++)
    {
        int n, sm1 = 0, sm2 = 0;
        cin >> n;
        for (int j = 0; j < n; j++)
        {
            int a;
            cin >> a;
            if (a % 2 == 0) sm1 += a;
            else sm2 += a;
        }
        if (sm1 > sm2) cout << "YES\n";
        else cout << "NO\n";
    }
}
