#include<bits/stdc++.h>
using namespace std;
char g[15][15];

void solve()
{
	int n;
	cin>>n;
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		{
			cin>>g[i][j];
		}
	}
	int sx=110,sy=110,ex=0,ey=0;
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		{
			if(g[i][j]=='1')
			{
				sx=min(sx,i);
				sy=min(sy,j);
				ex=max(ex,i);
				ey=max(ey,j);
			}
		}
	}
//	cout<<sx<<" "<<ex<<" "<<sy<<" "<<ey;
	for(int i=sx;i<=ex;i++)
	{
		for(int j=sy;j<=ey;j++)
		{
			if(g[i][j]=='0')
			{
				cout<<"TRIANGLE\n";
				return ;
			}
		}
	}
	cout<<"SQUARE\n";
}

int main()
{
	ios::sync_with_stdio(false);cin.tie(nullptr);cout.tie(nullptr);
	int t;
	cin>>t;
	while(t--)
	{
		solve();
	}

	return 0;
}
