#include<bits/stdc++.h>
using namespace std;
int main(){
    int t;
    cin >> t;
    while(t--){
        string init;
        cin >> init;
        if(init.substr(0, 2) == "00"){
            cout << 12 << ":" << init.substr(3, 2) << " " << "AM\n";
        }
        else if(atoi(init.substr(0, 2).c_str()) == 12){
            cout << init << " " << "PM\n";
        }
        else{
            if(atoi(init.substr(0, 2).c_str()) > 12){
                int num = atoi(init.substr(0, 2).c_str()) - 12;
                string s = to_string(num);
                if(s.size() == 1){
                    s = '0' + s;
                }
                cout << s << ":" << init.substr(3, 2) << " " << "PM\n";
            }
            else{
                cout << init << " " << "AM\n";
            }
        }
    }
}
