#include <iostream>

using namespace std;

const int N = 110;

int m;
string s[N];

int solve(string s, string t)
{
	int i;
	int res;

	for(i = res = 0; i < m; i ++)
		res += abs(s[i] - t[i]);

	return res;
}

int main()
{
	int t;
	int n;
	int i, j;
	int ans;

	cin >> t;
	while(t --)
	{
		cin >> n >> m;
		for(i = 1; i <= n; i ++)
			cin >> s[i];

		for(i = 1, ans = m * 26; i < n; i ++)
			for(j = i + 1; j <= n; j ++)
				ans = min(ans, solve(s[i], s[j]));

		cout << ans << endl;
	}

	return 0;
}
