#include<bits/stdc++.h>
#define int long long 
using namespace std;

void solve()
{
	vector<vector<char>> v(8,vector<char>(8));

	for(int i=0;i<8;i++)
	{
		 string s;
		 cin>>s;

		 for(int j=0;j<8;j++)
		 {
		 	 v[i][j]=s[j];
		 }
	}

	char ans='R';

	for(int i=0;i<8;i++)
	{
		 int f=0;
		 for(int j=0;j<8;j++)
		 {
		 	 if(v[i][j]=='R')
		 	 {
		 	 	 f++;
		 	 }
		 }

		 if(f==8)
		 	ans='R';
	}

	for(int j=0;j<8;j++)
	{
		 int f=0;
		 for(int i=0;i<8;i++)
		 {
		 	 if(v[i][j]=='B')
		 	 {
		 	 	 f++;
		 	 }
		 }

		 if(f==8)
		 	 ans='B';
	}

	cout<<ans<<endl;
}

int32_t main()
{
	 int t=1;

	 cin>>t;

	 while(t--)
	 {
	 	 solve();
	 }
	 return 0;
}
