using namespace std;

template<typename T1, typename T2>
ostream &operator<<(ostream &out, pair<T1, T2> &p) {
    out << p.first << ' ' << p.second;
    return out;
}

template<typename T1, typename T2>
istream &operator>>(istream &in, pair<T1, T2> &p) {
    in >> p.first >> p.second;
    return in;
}

template<typename T>
ostream &operator<<(ostream &out, vector<T> &arr) {
    for (auto &i: arr) out << i << ' ';
    return out;
}

template<typename T>
istream &operator>>(istream &in, vector<T> &arr) {
    for (auto &i: arr) in >> i;
    return in;
}

template<typename T>
ostream &operator<<(ostream &out, set<T> &arr) {
    for (auto &i: arr) out << i << ' ';
    return out;
}

template<typename T1, typename T2>
ostream &operator<<(ostream &out, map<T1, T2> &arr) {
    for (auto &i: arr) out << i << '\n';
    return out;
}

