void solve() {
    string s;
    cin >> s;
    int x;
    cin >> x;
    int h = (s[0] - '0') * 10 + (s[1] - '0');
    int m = (s[3] - '0') * 10 + (s[4] - '0');
    int sth = h, stm = m;
    int ans = 0;
    if ((m % 10) * 10 + m / 10 == h)
        ++ans;
    while(1) {
        m += x;
        h += m / 60;
        m %= 60;
        h %= 24;
        if (h == sth && stm == m)
