#include <iostream>

using namespace std;

const int N = 220;

long long a[N][N];
long long f[N * 2], g[N * 2];

int main()
{
	int t;
	int n, m;
	int i, j;
	long long ans;

	cin >> t;
	while(t --)
	{
		cin >> n >> m;
		for(i = 1; i <= n; i ++)
			for(j = 1; j <= m; j ++)
				cin >> a[i][j];

		for(i = 1; i <= n + m; i ++)
			f[i] = g[i] = 0;
		for(i = 1; i <= n; i ++)
			for(j = 1; j <= m; j ++)
			{
				f[i + j] += a[i][j];
				g[i - j + m] += a[i][j];
			}

		for(i = 1, ans = 0; i <= n; i ++)
			for(j = 1; j <= m; j ++)
				ans = max(ans, f[i + j] + g[i - j + m] - a[i][j]);

		cout << ans << endl;
	}

	return 0;
}
