#include<iostream>
#include<iomanip>
#include<cmath>
#include<cstring>
#include<cstdio>
#include<algorithm>
#include<queue>
using namespace std;
#define int long long
#define md(a) a=(a%mod+mod)%mod
#define file(a) freopen(#a".in","r",stdin);freopen(#a".out","w",stdout)
int T;int n,Q;
int a[1005][1005],sum[1005][1005];
int ask(int xa,int ya,int xb,int yb)
{
	if(xa>xb||ya>yb)return 0;
	return sum[xb][yb]-sum[xa-1][yb]-sum[xb][ya-1]+sum[xa-1][ya-1];
}
void solve()
{
	scanf("%lld%lld",&n,&Q);
	for(int i=1;i<=1000;i++)for(int j=1;j<=1000;j++)a[i][j]=sum[i][j]=0;
	for(int i=1;i<=n;i++)
	{
		int x,y;scanf("%lld%lld",&x,&y);
		a[x][y]+=x*y;
	}
	for(int i=1;i<=1000;i++)
	{
		for(int j=1;j<=1000;j++)
		{
			sum[i][j]=sum[i-1][j]+sum[i][j-1]-sum[i-1][j-1]+a[i][j];
		}
	}
	while(Q--)
	{
		int xa,ya,xb,yb;scanf("%lld%lld%lld%lld",&xa,&ya,&xb,&yb);
		printf("%lld\n",ask(xa+1,ya+1,xb-1,yb-1));
	}
}
signed main()
{
	scanf("%lld",&T);
	while(T--)solve();
	return 0;
}
