#include<bits/stdc++.h>
#define int long long 
using namespace std;
#define vi vector<int>

void solve()
{
	int n,q;
	cin>>n>>q;

	vi v(n);
	for(int i=0;i<n;i++)
		 cin>>v[i];

		vi dp(n);
		dp[0]=v[0];

		for(int i=1;i<n;i++)
			 dp[i]=max(v[i],dp[i-1]);

		vi pre(n,0);
		pre[0]=v[0];

		for(int i=1;i<n;i++)
			 pre[i]=pre[i-1]+v[i];

		while(q--)
		{
			 int a;
			 cin>>a;

			 int index=upper_bound(dp.begin(),dp.end(),a)-dp.begin();

			 index--;
			 if(index<0)
			 	 cout<<"0"<<" ";
			 else
			 	cout<<pre[index]<<" ";
		}

		cout<<endl;
}

int32_t main()
{
	 int t=1;

	 cin>>t;

	 while(t--)
	 {
	 	 solve();
	 }
	 return 0;
}
