#include<bits/stdc++.h>
using namespace std;

void solve()
{
	int n,k;
	cin>>n>>k;
	vector<int>v;
	while(n)
	{
		v.push_back((n+1)/2);
		n/=2;
	}
	int now=0,mpow=1;
	for(auto i:v)
	{
		if(now<k&&k<=now+i)
		{
			cout<<mpow*(2*(k-now)-1)<<"\n";
			return;
		}
		now+=i;
		mpow*=2;
	}
}

int main()
{
	ios::sync_with_stdio(false);cin.tie(nullptr);cout.tie(nullptr);
	int t;
	cin>>t;
	while(t--)
	{
		solve();
	}

	return 0;
}
