#include <bits/stdc++.h>
using namespace std;
char b[101010],c[201010];
int main(){
	int T;scanf("%d",&T);
	while(T--){
		scanf("%s%s",b,c);
		int n=strlen(b),m=strlen(c);
		reverse(b,b+n);reverse(c,c+m);
		int s=0;
		while(c[s]=='0')s++;
		int s2=s;
		while(b[s2]=='0')s2++;
		int j=s2-s;
		printf("%d\n",j);
	}
}
