#include<cstdio>
#include<iostream>
using namespace std;
#define N 122
char s[N];
int main()
{
	int T,n,ans;
	scanf("%d",&T);
	while(T--)
	{
		scanf("%d%s",&n,s+1);
		for (int i=1;i<=n;++i) if (s[i]=='>') {ans=i-1;break;}
		for (int i=n;i;--i) if (s[i]=='<') {ans=min(ans,n-i);break;}
		printf("%d\n",ans);
	}
	return 0;
}
