#include<bits/stdc++.h>
using namespace std;
//dengyaotriangle!

int main(){
    ios::sync_with_stdio(0);cin.tie(0);
    int t;
    cin>>t;
    while(t--){
        int n,k;
        cin>>n>>k;
        vector<int> p(n);
        for(int i=0;i<n;i++)cin>>p[i];
        long long s=p[0];
        long long ans=0;
        for(int i=1;i<n;i++){
            ans=max(ans,(100ll*p[i]+k-1)/k-s);
            s+=p[i];
        }
        cout<<ans<<'\n';
    }
    return 0;
}
