using namespace std;
typedef long long ll;

int main() {
    int t;
    scanf("%d", &t);
    for (int tc = 1; tc <= t; tc++) {
        int a, b;
        scanf("%d %d", &a, &b);
        int ans = 1;
        if ((a + b) % 3 != 0) ans = 0;
        int xay = (a + b) / 3;
        int xsy = a - b;
        int x2 = xay + xsy, y2 = xay - xsy;
        if (x2 % 2 != 0 || x2 < 0) ans = 0;
        if (y2 % 2 != 0 || y2 < 0) ans = 0;
        printf("%s\n", ans ? "YES" : "NO");
    }
}
