#include<bits/stdc++.h>
#define int long long
using namespace std;
inline int read()
{
	int n=0,f=1,ch=getchar();
	while(ch<'0'||ch>'9')
	{
		if(ch=='-')f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9')
	{
		n=n*10+ch-'0';
		ch=getchar();
	}
	return n*f;
}
int a[500005];
signed main()
{
	int t,ans;
	t=read();
	for(int greg=1;greg<=t;greg++)
	{
		for(int i=1;i<=4;i++)a[i]=read();
		if(a[1]==0)
		{
			printf("1\n");
			continue;
		}
		ans=a[1];
		int x,y;
		x=a[1];
		y=a[1];
		int sth=min(a[2],a[3]);
		ans+=2*sth;
		a[2]-=sth;
		a[3]-=sth;
		ans+=min(a[2]+a[3]+a[4],x+1);
		printf("%lld\n",ans);
	}
	return 0;
}
