typedef long long ll;
typedef long double ld;
typedef unsigned long long ull;

using namespace std;

ll slv(vector<ll> a, int k) {
    sort(a.rbegin(), a.rend());
    ll ans = 0;
    for (int i = 0; i < min(int(a.size()), k); i++) {
        ans += a[i];
    }
    return ans;
}

const int MX = 200 * 1000 + 7;

ll a[MX];

int main() {
#ifdef FASTIO
    ios_base::sync_with_stdio(false), cin.tie(0), cout.tie(0);
#endif
    int n, k;
    cin >> n >> k;
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }
    string s;
    cin >> s;
    int l = 0;
    ll ans=  0;
    while (l < n) {
        int r = l;
        while (r < n && s[l] == s[r]) {
            r++;
        }
        vector<ll> go;
        for (int i = l; i < r; i++) {
            go.push_back(a[i]);
        }
        ans += slv(go, k);
        l = r;
    }
    cout << ans << "\n";
}
