#include <bits/stdc++.h>

using namespace std;
typedef long long ll;

int main() {
    int t;
    scanf("%d", &t);
    for (int tc = 1; tc <= t; tc++) {
        int a, b, k;
        scanf("%d %d %d", &a, &b, &k);
        int g = __gcd(a, b);
        a /= g;
        b /= g;
        if (a > b) swap(a, b);
        if ((b - 1 + a - 1) / a >= k) printf("REBEL\n");
        else printf("OBEY\n");
    }
}
