#include <bits/stdc++.h>

using namespace std;
#define int long long
int32_t main()
{
    ios_base::sync_with_stdio(false);cin.tie(0);cout.tie(0);
    int t;cin>>t;
    while(t--)
    {
        string s;cin>>s;int n=s.size();
        int m;cin>>m;
        string l,r;cin>>l>>r;
        int nxt[n+1][10];
        for(int i=0;i<10;++i) nxt[n][i]=n;
        for(int i=n-1;i>=0;--i)
        {
            for(int j=0;j<10;++j)
            {
                nxt[i][j]=nxt[i+1][j];
                if(j==(s[i]-'0'))
                {
                    nxt[i][j]=i;
                }
            }
        }
        int ma=0;
        for(int i=0;i<m;++i)
        {
            int ma1=0;
            for(int j=l[i]-'0';j<=r[i]-'0';++j)
            {
                ma1=max(ma1,nxt[ma][j]+1);
            }
            ma=ma1;
            if(ma1>n) break;
        }
        puts(ma>n ? "YES" : "NO");
    }
    return 0;
}
