#include<cstdio>
#include<algorithm>
int n,w,a[107],b[107];
int main(){
	scanf("%d%d",&n,&w);
	for(int i=1;i<=n;++i)scanf("%d",a+i),w-=b[i]=a[i]+1>>1;
	if(w<0)return puts("-1"),0;
	while(w){
		int mw=0;
		for(int i=1;i<=n;++i)if((!mw||a[i]>=a[mw])&&a[i]>b[i])mw=i;
		int x=std::min(a[mw]-b[mw],w);
		b[mw]+=x;
		w-=x;
	}
	for(int i=1;i<=n;++i)printf("%d ",b[i]);
	return 0;
}
