#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
template<class T>bool chmax(T &a, const T &b) { if (a<b) { a=b; return true; } return false; }
template<class T>bool chmin(T &a, const T &b) { if (b<a) { a=b; return true; } return false; }
#define all(x) (x).begin(),(x).end()
#define fi first
#define se second
#define mp make_pair
#define si(x) int(x.size())
const int mod=998244353,MAX=300005,INF=1<<30;

int main(){

    std::ifstream in("text.txt");
    std::cin.rdbuf(in.rdbuf());
    cin.tie(0);
    ios::sync_with_stdio(false);

    int Q;cin>>Q;
    while(Q--){
        ll N,K;cin>>N>>K;
        vector<ll> L(N),R(N);
        ll sum=0;
        for(int i=0;i<N;i++) cin>>L[i];
        for(int i=0;i<N;i++) cin>>R[i];

        for(int i=0;i<N;i++){
            sum+=R[i]-L[i]+1;
        }
        if(sum<K){
            cout<<-1<<"\n";
            continue;
        }

        ll ans=(1LL<<60),ONE=0;
        sum=0;

        int i=0;
        for(ll tar=K;tar<=K+N;tar++){
            while(i<N){
                if(sum+R[i]-L[i]+1>=tar){
                    ll need=L[i]+(tar-sum-1)+2*(i+1);
                    need-=2*min(ONE,tar-K);
                    chmin(ans,need);
                    break;
                }else{
                    sum+=R[i]-L[i]+1;
                    if(L[i]==R[i]) ONE++;
                    i++;
                }
            }
        }

        cout<<ans<<"\n";
    }
}
