#include <bits/stdc++.h>
using namespace std;
const int MAX = 10000000;
int main(){
  ios::sync_with_stdio(false);
  cin.tie(nullptr);
  vector<int> p(MAX, -1);
  for (int i = 2; i < MAX; i++){
    if (p[i] == -1){
      for (int j = i * 2; j < MAX; j += i){
        p[j] = i;
      }
    }
  }
  int n;
  cin >> n;
  for (int i = 0; i < n; i++){
    int x, y;
    cin >> x >> y;
    if (gcd(x, y) > 1){
      cout << 0 << endl;
    } else if (y == x + 1){
      cout << -1 << endl;
    } else {
      int d = y - x;
      vector<int> f;
      while (p[d] != -1){
        f.push_back(p[d]);
        d /= p[d];
      }
      if (d > 1){
        f.push_back(d);
      }
      sort(f.begin(), f.end());
      f.erase(unique(f.begin(), f.end()), f.end());
      int ans = MAX;
      for (int a : f){
        ans = min(ans, a - x % a);
      }
      cout << ans << "\n";
    }
  }
}
