void run_case() {
    int N, X;
    cin >> N >> X;
    vector<int> A(N);

    for (auto &a : A)
        cin >> a;

    int last = -1;

    for (int i = 0; i < N - 1; i++)
        if (A[i] > A[i + 1])
            last = i;

    int ops = 0;

    for (int i = 0; i <= last; i++)
        if (A[i] > X) {
            ops++;
            swap(A[i], X);
        }

    if (!is_sorted(A.begin(), A.end()))
        ops = -1;

    cout << ops << '\n';
}

int main() {
    ios::sync_with_stdio(false);
#ifndef NEAL_DEBUG
    cin.tie(nullptr);
#endif

    int tests;
    cin >> tests;

    while (tests-- > 0)
        run_case();
}
