#include <bits/stdc++.h>

using namespace std;

#ifdef LOCAL
#include "algo/debug.h"
#else
#define debug(...) 42
#endif

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  int tt;
  cin >> tt;
  while (tt--) {
    int n, k;
    cin >> n >> k;
    vector<int> a(n, -1);
    vector<int> c(n, -1);
    int cnt = 0;
    int beg = 0;
    while (beg < n) {
      k = min(k, n - beg);
      int half = (k - 1) / 2;
      for (int i = 0; i < k; i++) {
        a[beg + i] = beg + (half - 1 - i);
        if (a[beg + i] < beg) {
          a[beg + i] += k;
        }
        c[beg + i] = cnt;
      }
      cnt += 1;
      beg += k;
    }
    for (int i = 0; i < n; i++) {
      cout << a[i] + 1 << " \n"[i == n - 1];
    }
    cout << cnt << '\n';
    for (int i = 0; i < n; i++) {
      cout << c[i] + 1 << " \n"[i == n - 1];
    }
  }
  return 0;
}
