#include <bits/stdc++.h>
#define mod 998244353
#define int long long
using namespace std;
vector<int> e[200005];
int f[200005],FA[200005];
multiset<int> S,sf[200005];
int ans;
void mv(int u,int v)
{
	if(f[u]==f[v]+1)
	{
		if(sf[u].size()>1)
		{
			f[u]=(*++sf[u].begin())+1;
			S.erase(S.find(f[u]-1));
		}
		else f[u]=1;
	}
	else S.erase(S.find(f[v]));
	sf[u].erase(sf[u].find(f[v]));
	FA[u]=v,FA[v]=0;
	sf[v].insert(f[u]);
	if(*sf[v].begin()==f[u])
	{
		if(sf[v].size()>1)
			S.insert(*++sf[v].begin());
	}
	else S.insert(f[u]);
	f[v]=(*sf[v].begin())+1;
}
void dfs2(int u,int fa)
{
	if(S.size()) ans=max(ans,min(f[u],*S.begin()));
	else ans=max(ans,f[u]);
	for(auto v:e[u])
	{
		if(v==fa) continue;
		mv(u,v),dfs2(v,u),mv(v,u);
	}
}
void dfs1(int u,int fa)
{
	FA[u]=fa;
	vector<int> ss;
	for(auto v:e[u])
		if(v!=fa) dfs1(v,u),ss.push_back(f[v]);
	sort(ss.begin(),ss.end());
	if(ss.size()) f[u]=ss[0]+1;
	else f[u]=1;
	for(auto t:ss) sf[u].insert(t);
	for(int i=1;i<ss.size();i++) S.insert(ss[i]);
}
signed main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--)
	{
		S.clear();
		int n;
		cin >> n;
		for(int i=0;i<=n;i++) e[i].clear(),sf[i].clear(),f[i]=0;
		for(int i=1;i<n;i++)
		{
			int u,v;
			cin >> u >> v;
			e[u].push_back(v);
			e[v].push_back(u);
		}
		ans=0;
		dfs1(1,0);
	/*	for(int i=1;i<=n;i++) cout << f[i] << " ";
		cout << "\n";
		mv(1,2);
		for(int i=1;i<=n;i++) cout << f[i] << " ";
		cout << "\n";*/
		dfs2(1,0);
		cout << ans << "\n";
	}
	return 0;
}
