#include<bits/stdc++.h>
#define int long long
using namespace std;
inline int read()
{
	int n=0,f=1,ch=getchar();
	while(ch<'0'||ch>'9')
	{
		if(ch=='-')f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9')
	{
		n=n*10+ch-'0';
		ch=getchar();
	}
	return n*f;
}
int pri[100005],tsl[100005],cnt;
int ys[500005],qans[500005],tmp;
map<int,int>ma;
void dfs(int x,int now)
{
	if(x==cnt+1)
	{
		ys[++tmp]=now;
		return;
	}
	for(int i=0;i<=tsl[x];i++)
	{
		dfs(x+1,now);
		now*=pri[x];
	}
}
signed main()
{
	int t,n,m1,m2;
	t=read();
	for(int greg=1;greg<=t;greg++)
	{
		n=read();
		m1=read();
		m2=read();
		cnt=0;
		for(int i=2;i*i<=m1||i*i<=m2;i++)
		{
			if(m1%i!=0&&m2%i!=0)continue;
			pri[++cnt]=i;
			tsl[cnt]=0;
			while(m1%i==0)m1/=i,tsl[cnt]++;
			while(m2%i==0)m2/=i,tsl[cnt]++;
		}
		if(m1<m2)swap(m1,m2);
		if(m1>1)
		{
			pri[++cnt]=m1;
			tsl[cnt]=1;
		}
		if(m2>1)
		{
			if(m1==m2)tsl[cnt]++;
			else
			{	
				pri[++cnt]=m2;
				tsl[cnt]=1;
			}
		}
		tmp=0;
		dfs(1,1);
		sort(ys+1,ys+tmp+1);
		ma.clear();
		for(int i=1;i<=tmp;i++)ma[ys[i]]=i;
		for(int i=1;i<=tmp;i++)
		{
			if(ys[i]<=n)
			{
				qans[i]=ys[i];
				continue;
			}
			qans[i]=-1;
			for(int j=1;j<=cnt;j++)
			{
				if(ys[i]%pri[j]==0)qans[i]=max(qans[i],qans[ma[ys[i]/pri[j]]]);
			}
		}
		int sl=0,yh=0;
		for(int i=1;i<=tmp;i++)
		{
			if(qans[i]!=0&&ys[i]/qans[i]<=n)
			{
				//printf("%lld %lld\n",ys[i],ys[i]/qans[i]);
				sl++;
				yh^=(ys[i]/qans[i]); 
			}
		}
		printf("%lld %lld\n",sl,yh);
	}
	return 0;
}
