#include <bits/stdc++.h>
using namespace std;
int main(){
	int a,b;scanf("%d%d",&a,&b);
	int n=a+b;
	if(a==0||b==0)return puts("1"),0;
	int ans=0;
	for(int i=1,j;i<=n;i=j){
		int q=n/i;
		j=n/q+1;
		int la=(a+q)/(q+1),ra=a/q,lb=(b+q)/(q+1),rb=b/q;
		if(la>ra||lb>rb)continue;
		la+=lb,ra+=rb;
		la=max(la,i);
		ra=min(ra,j-1);
		ans+=ra-la+1;
	}
	printf("%d\n",ans);
}
