#include<bits/stdc++.h>
#define int long long
#define mod 998244353
using namespace std;
inline int read()
{
	int n=0,f=1,ch=getchar();
	while(ch<'0'||ch>'9')
	{
		if(ch=='-')f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9')
	{
		n=n*10+ch-'0';
		ch=getchar();
	}
	return n*f;
}
int dp[5005],het[5005];
int jc[5005],njc[5005];
int ksm(int n,int k)
{
	int ans=1;
	while(k>=1)
	{
		if(k&1)ans=1LL*ans*n%mod;
		k>>=1;
		n=1LL*n*n%mod;
	}
	return ans;
}
signed main()
{
	int n;
	n=read();
	jc[0]=1;
	for(int i=1;i<=n;i++)jc[i]=1LL*i*jc[i-1]%mod;
	njc[n]=ksm(jc[n],mod-2);
	for(int i=n-1;i>=0;i--)njc[i]=1LL*(i+1)*njc[i+1]%mod;
	dp[1]=1;
	het[1]=1;
	dp[2]=1;
	het[2]=2;
	for(int i=3;i<=n;i++)
	{
		for(int j=1;j<=i-1;j++)
		{
			dp[i]=(dp[i]+1LL*dp[j]*het[i-j]%mod*jc[i-1]%mod*njc[j-1]%mod*njc[i-j]%mod)%mod;
		}
		het[i]=(2*dp[i])%mod;
	}
	printf("%lld\n",(2LL*(dp[n]+mod-1)%mod)%mod);
	return 0;
}
