		}
		typec dot(Point2D p){
			return x*p.x+y*p.y;
		}
		typec det(Point2D p){
			return x*p.y-y*p.x;
		}
		typec rad(Point2D a,Point2D b){
			Point2D p=*this;
			return fabs(atan2(fabs(a.sub(p).det(b.sub(p))),a.sub(p).dot(b.sub(p))));
		}
		Point2D trunc(typec r){
			typec l=len();
			if (!dblcmp(l))return *this;
			r/=l;
			return Point2D(x*r,y*r);
		}
